        correct: 2,
    },
    {
        title: 'Какой-то вопрос 1?',
        variants: ['ответ', 'ответ', 'ответ'],
        correct: 0,
    },
];

function Game({ question, onClickVariant }){
    return(
        <>
        <div className={s.progress}>
            <div className={s.progressBarBrd}>
            <div style={{width: '20%'}} className={s.progressBar}></div>
            </div>
            <h1>{question.title}</h1>
            <ul>
                {
                    question.variants.map(text, index => 
                    <li onClick={()=>onClickVariant(index)} key={text}>{text}</li>)
                }
            </ul>
        </div>
        </>
    )
}

function Result(){
    return(
        <div className={s.result}>
            <img></img>
            <h2>Вы ответили правильно</h2>
            <button>Попробовать снова</button>
        </div>
    )
}

