import s from './ProjectAnimation.module.css'
import React from 'react';

const questions = [
    {
        title: 'Какой-то вопрос 1?',
        variants: ['ответ', 'ответ', 'ответ'],
        correct: 0,
    },
    {
        title: 'Какой-то вопрос 1?',
        variants: ['ответ', 'ответ', 'ответ'],
        correct: 1,
    },
    {
        title: 'Какой-то вопрос 1?',
        variants: ['ответ', 'ответ', 'ответ'],
        correct: 2,
    },
    {
        title: 'Какой-то вопрос 1?',
        variants: ['ответ', 'ответ', 'ответ'],
        correct: 0,
    },
];

function Game(){
    const [step, setStep] = React.useState(0);
    const question = questions[step];
    console.log(question)
    return(
        <>
        <div className={s.progress}>
            <div className={s.progressBarBrd}>
            <div style={{width: '20%'}} className={s.progressBar}></div>
            </div>
            <h1>Какаойто вопрос?</h1>
            <ul>
                <li>Какой-то ответ</li>
                <li>Какой-то ответ</li>
                <li>Какой-то ответ</li>
            </ul>
        </div>
        </>
    )
}

function Result(){
    return(
        <div className={s.result}>
            <img></img>
            <h2>Вы ответили правильно</h2>
            <button>Попробовать снова</button>
        </div>
    )
}

function ProjectAnimation(){
    return(
        <div className={s.app}>
            <Game question={question} />
        </div>
    )
}

export default ProjectAnimation;
